
"use strict"




var doc = `
Usage:
	ht freq ((-y | --year) | (-m | --month)) [--] <phrase> ...

Options:

	-y, --year     display average term usage by year.
	-m, --month    display average term usage by month.

Arguments:

	<phrase>     a phrase to examine usage of.

Description:
`





var docopt = require('docopt').docopt
var args   = docopt(doc)
var main   = require('hacker-terms/app/main')




main(args)
