
"use strict"




var commons = require('hacker-terms/commons/commons')




var findTermFrequency = (args, data, callback) => {

	var summaries = data.map(datum => {

		var words   = commons.toWords(datum.title)

		var summary = {
			terms: { },
			datum
		}

		args.phrases.forEach(phrase => {
			summary.terms[phrase] = words.indexOf(phrase) === -1 ? 0 : 1
		})

		return summary

	})

	callback(summaries)

}




module.exports = findTermFrequency
